// src/pages/Cart.jsx
import React, { useState } from 'react';
import Header from '../components/Header';
import CartTable from '../components/CartTable';
import Footer from '../components/Footer';
import { useNavigate } from 'react-router-dom';

const Cart = () => {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const navigate = useNavigate();

  const handleAuthClick = () => {
    if (isLoggedIn) {
      setIsLoggedIn(false);
      // Optional: redirect or other actions on sign out
    } else {
      navigate('/signin');
    }
  };

  return (
    <>
      <Header isLoggedIn={isLoggedIn} onAuthClick={handleAuthClick} />
      <div className="cart-container" style={{ padding: '20px', maxWidth: '960px', margin: 'auto' }}>
        <hr />
        <CartTable />
        <hr />
        <div style={{ marginTop: '1rem' }}>
            
          <button
  className={`checkout-btn ${isCartEmpty ? 'disabled' : ''}`}
  onClick={() => navigate('/checkout')}
  disabled={isCartEmpty}
>
  Proceed To Checkout
</button>


        </div>
      </div>
      <Footer />
    </>
  );
};

export default Cart;
