import React, { useEffect, useState } from "react";
import "../styles/checkout.css";
import Header from "../components/Header";
import Footer from "../components/Footer";
import { useNavigate } from "react-router-dom";

const Checkout = () => {
  const SHIPPING_COST = 50;
  const navigate = useNavigate();
  const [discountApplied, setDiscountApplied] = useState(false);
  const [subtotal, setSubtotal] = useState(0);
  const [total, setTotal] = useState(0);
  const cartItems = [
    { id: 1, name: "Game A", price: 950 },
    { id: 2, name: "Game B", price: 300 },
  ];

  useEffect(() => {
    let sum = cartItems.reduce((acc, item) => acc + item.price, 0);
    setSubtotal(sum);
    setTotal(discountApplied ? (sum + SHIPPING_COST) * 0.95 : sum + SHIPPING_COST);
  }, [discountApplied]);

  const applyDiscount = () => {
    const code = document.getElementById("discount-code").value.trim().toLowerCase();
    if (code === "taher20") {
      alert("Congratulations! You got 5% discount.");
      setDiscountApplied(true);
    } else {
      alert("Invalid discount code");
      setDiscountApplied(false);
    }
  };

  return (
    <>
      <Header />
      <div className="cart-container">
        <div className="breadcrumb">
          <a href="/">Home</a> | <a href="/cart">Cart</a> | Checkout and payment
        </div>
        <hr />
        <div className="checkout-container">
          {/* LEFT FORM */}
          <div className="checkout-left">
            <h2>Contact</h2>
            <input type="email" placeholder="Email or mobile phone number" required />
            <label><input type="checkbox" /> Email me with news and offers</label>

            <h2>Delivery</h2>
            <select><option>Country/Region</option><option selected>Egypt</option></select>
            <div className="input-group">
              <input type="text" placeholder="First name" />
              <input type="text" placeholder="Last name" />
            </div>
            <input type="text" placeholder="Address" />
            <input type="text" placeholder="Apartment, suite, etc." />
            <div className="input-group">
              <input type="text" placeholder="City" />
              <select>
                <option>Cairo</option>
                <option>Giza</option>
                <option>New Cairo</option>
                <option>Nasr City</option>
              </select>
              <input type="text" placeholder="Postal code (optional)" />
            </div>
            <input type="tel" placeholder="Phone" />
            <label><input type="checkbox" /> Save this information for next time</label>
          </div>

          {/* RIGHT SUMMARY */}
          <div className="checkout-right">
            <div className="cart-summary">
              <div id="cart-items">
                {cartItems.map(item => (
                  <div className="cart-item" key={item.id}>
                    <p>{item.name} - <strong>EGP {item.price}</strong></p>
                  </div>
                ))}
              </div>

              <div className="discount-code">
                <input type="text" id="discount-code" placeholder="Discount code" />
                <button onClick={applyDiscount}>Apply</button>
              </div>

              <div className="price-row"><span>Subtotal</span><span>EGP {subtotal}</span></div>
              <div className="price-row"><span>Shipping</span><span>EGP {SHIPPING_COST}</span></div>
              <div className="price-total"><strong>Total</strong><strong>EGP {total.toFixed(2)}</strong></div>

              <h2>Payment</h2>
              <p className="secure-text">All transactions are secure and encrypted.</p>
              <label><input type="radio" name="payment" defaultChecked /> Credit card</label>
              <div className="cards">
                <img src="https://img.icons8.com/color/48/000000/visa.png" />
                <img src="https://img.icons8.com/color/48/000000/mastercard-logo.png" />
              </div>
              <input type="text" placeholder="Card number" />
              <div className="input-group">
                <input type="text" placeholder="Expiration date (MM / YY)" />
                <input type="text" placeholder="Security code" />
              </div>
              <input type="text" placeholder="Name on card" />
              <label><input type="radio" name="payment" /> Cash on Delivery (COD)</label>
              <label><input type="checkbox" /> Use shipping address as billing address</label>

              <div className="pay-now">
                <button onClick={() => navigate("/orders")}>Pay now</button>
              </div>
            </div>
          </div>
        </div>
      </div>
      <Footer />
    </>
  );
};

export default Checkout;
